import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './InwardUpload.css';

const InwardUpload = () => {
  const [inwardType, setInwardType] = useState('Internal');
  const [letterDate, setLetterDate] = useState('');
  const [receivedDate, setReceivedDate] = useState('');
  const [subject, setSubject] = useState('');
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [address, setAddress] = useState('');
  const [briefDescription, setBriefDescription] = useState('');
  const [descriptionLanguage, setDescriptionLanguage] = useState('English');
  const [numberOfPages, setNumberOfPages] = useState('');
  const [pdfPath, setPdfPath] = useState('');
  const [remark, setRemark] = useState('');

  const navigate = useNavigate();

  const handleUpload = () => {
    console.log('Upload clicked');
  };

  const handleLanguageChange = (e) => {
    const selectedLanguage = e.target.value;
    setDescriptionLanguage(selectedLanguage);
    
    if (selectedLanguage === 'Hindi/Marathi') {
      window.open('http://quillpad.com');
    }
  };

  const goBackToMain = () => {
    navigate('/main');
  };

  return (
    <div className={`inward-upload-container ${inwardType === 'Internal' ? 'form-internal' : 'form-external'}`}>
      <h2>Inward Upload</h2>

      {/* Radio Button for Inward Type */}
      <div className="form-group">
        <label>Select Inward Type:</label>
        <div className="radio-buttons">
          <label>
            <input
              type="radio"
              value="Internal"
              checked={inwardType === 'Internal'}
              onChange={(e) => setInwardType(e.target.value)}
            />
            Internal
          </label>
          <label>
            <input
              type="radio"
              value="External"
              checked={inwardType === 'External'}
              onChange={(e) => setInwardType(e.target.value)}
            />
            External
          </label>
        </div>
      </div>

      {/* Internal Form */}
      {inwardType === 'Internal' && (
        <form>
          <div className="form-group">
            <label>Select Letter Date:</label>
            <input type="date" value={letterDate} onChange={(e) => setLetterDate(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Select Received Date:</label>
            <input type="date" value={receivedDate} onChange={(e) => setReceivedDate(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Subject:</label>
            <input type="text" value={subject} onChange={(e) => setSubject(e.target.value)} />
          </div>
          <div className="form-group">
            <label>From:</label>
            <input type="text" value={from} onChange={(e) => setFrom(e.target.value)} />
          </div>
          <div className="form-group">
            <label>To:</label>
            <input type="text" value={to} onChange={(e) => setTo(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Address:</label>
            <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Brief Description:</label>
            <input type="text" value={briefDescription} onChange={(e) => setBriefDescription(e.target.value)} />
            <select value={descriptionLanguage} onChange={handleLanguageChange}>
              <option value="English">English</option>
              <option value="Hindi/Marathi">Hindi/Marathi</option>
            </select>
          </div>
          <div className="form-group">
            <label>Number of Pages:</label>
            <input type="number" value={numberOfPages} onChange={(e) => setNumberOfPages(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Choose PDF Path:</label>
            <input type="file" />
          </div>
          <div className="form-group">
            <label>Remark:</label>
            <input type="text" value={remark} onChange={(e) => setRemark(e.target.value)} />
          </div>
          <button type="button" onClick={handleUpload}>Upload Internal</button>
        </form>
      )}

      {/* External Form */}
      {inwardType === 'External' && (
        <form>
          <div className="form-group">
            <label>Select Letter Date:</label>
            <input type="date" value={letterDate} onChange={(e) => setLetterDate(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Select Received Date:</label>
            <input type="date" value={receivedDate} onChange={(e) => setReceivedDate(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Subject:</label>
            <input type="text" value={subject} onChange={(e) => setSubject(e.target.value)} />
          </div>
          <div className="form-group">
            <label>From:</label>
            <input type="text" value={from} onChange={(e) => setFrom(e.target.value)} />
          </div>
          <div className="form-group">
            <label>To:</label>
            <input type="text" value={to} onChange={(e) => setTo(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Address:</label>
            <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Brief Description:</label>
            <input type="text" value={briefDescription} onChange={(e) => setBriefDescription(e.target.value)} />
            <select value={descriptionLanguage} onChange={handleLanguageChange}>
              <option value="English">English</option>
              <option value="Hindi/Marathi">Hindi/Marathi</option>
            </select>
          </div>
          <div className="form-group">
            <label>Number of Pages:</label>
            <input type="number" value={numberOfPages} onChange={(e) => setNumberOfPages(e.target.value)} />
          </div>
          <div className="form-group">
            <label>Choose PDF Path:</label>
            <input type="file" />
          </div>
         // <div className="form-group">
            <label>Remark:</label>
            <input type="text" value={remark} onChange={(e) => setRemark(e.target.value)} />
          </div>
          <button type="button" onClick={handleUpload}>Upload External</button>
        </form>
      )}//

      <button onClick={goBackToMain}>Go Back to Main</button>
    </div>
  );
};

export default InwardUpload;
